import java.util.ArrayList;

/*
Problem

Corona Virus is spreading and many people got infected by it and
were admitted to a hospital. There are N beds in a hall in the hospital.
If the ith bed is used by the patient then it is marked as 1 else 0. You have to partition the hall into different
 sections by putting a temporary wall in such a way that each section must have exactly two patients.
 You have to find the total number of ways to partition the hall. Print the answer modulo 1000000007.

 */

class HallPartitioner {
    private final int mod = 1000000007;

    private long findWays(String rep, int beds, int k) {
        ArrayList<Integer> onePosList = new ArrayList<>();
        long numWays = 1L;

        for (int i = 0; i < beds; i++) {
            if (rep.charAt(i) == '1') {
                onePosList.add(i);
            }
        }

        if (onePosList.size() == 0 || ((onePosList.size() % k) != 0)) {
            return -1;
        }

        for (int i = k; i < onePosList.size(); i += k) {
            numWays = (numWays * ((onePosList.get(i) % mod) - (onePosList.get(i - 1) % mod))) % mod;
        }

        return numWays;
    }

    public long findPartitions(int beds, String rep) {
        return findWays(rep, beds, 2);
    }


    public static void main(String[] args) {
        HallPartitioner hallPartitioner = new HallPartitioner();
        System.out.println(hallPartitioner.findPartitions(6, "110011"));
        System.out.println(hallPartitioner.findPartitions(9210, ""));
        System.out.println(hallPartitioner.findPartitions(1196, ""));
        System.out.println(hallPartitioner.findPartitions(5893, ""));
    }
}
